mapper.select:
	SELECT 
			TAII.ID,
			TAII.VALID,
			TAII.PARENTID,
			TAII.DESCRIPTION,
			TAII.EDITABLE,
			TAII.VIEWABLE,
			TAII.NAME,
			TAII.AUTHTYPE
	  FROM T_AUTH_AUTHITEM TAII
	
		  
            AND TAII.ID = #{id,javaType=java.lang.String}
        
		  
            AND TAII.VALID = #{valid,javaType=boolean}
        
		  
            AND TAII.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TAII.DESCRIPTION = #{description,javaType=java.lang.String}
        
		  
            AND TAII.EDITABLE = #{editAble,javaType=boolean}
        
		  
            AND TAII.VIEWABLE = #{viewAble,javaType=boolean}
        
		  
            AND TAII.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TAII.AUTHTYPE = #{authType,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM T_AUTH_AUTHITEM TAII
	
		  
            AND TAII.ID = #{id,javaType=java.lang.String}
        
		  
            AND TAII.VALID = #{valid,javaType=boolean}
        
		  
            AND TAII.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TAII.DESCRIPTION = #{description,javaType=java.lang.String}
        
		  
            AND TAII.EDITABLE = #{editAble,javaType=boolean}
        
		  
            AND TAII.VIEWABLE = #{viewAble,javaType=boolean}
        
		  
            AND TAII.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TAII.AUTHTYPE = #{authType,javaType=java.lang.String}
