CREATE TABLE CORE_CONFIG_CONTEXT_BAK
	 AS SELECT * FROM CORE_CONFIG_CONTEXT;
DROP TABLE CORE_CONFIG_CONTEXT;
CREATE TABLE CORE_CONFIG_CONTEXT(
	ID VARCHAR2(64) NOT NULL,
	SYSTEMID VARCHAR2(64) NOT NULL,
	KEY VARCHAR2(64) NOT NULL,
	NAME VARCHAR2(64) NOT NULL,
	DESCRIPTION VARCHAR2(512),
	CREATEDATE TIMESTAMP,
	LASTUPDATEDATE TIMESTAMP,
	VALIDATEEXPRESSION VARCHAR2(128),
	PRIMARY KEY(ID)
);
CREATE UNIQUE INDEX IDX_CORE_CONF_CONT_00 ON CORE_CONFIG_CONTEXT(KEY,SYSTEMID);

