mapper.select:
	SELECT 
			TWFTASKDEF.ID,
			TWFTASKDEF.TASKTYPE,
			TWFTASKDEF.WFTKDID,
			TWFTASKDEF.SERVICETYPE,
			TWFTASKDEF.PARENTID,
			TWFTASKDEF.TASKORDER AS order,
			TWFTASKDEF.VIEWABLE,
			TWFTASKDEF.NAME,
			TWFTASKDEF.ALISE,
			TWFTASKDEF.WFDID,
			TWFTASKDEF.WFTKDKEY AS key
	  FROM WF_TASK_DEF TWFTASKDEF
	
		  
            AND TWFTASKDEF.ID = #{id,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.TASKTYPE = #{taskType,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFTKDID = #{wftkdId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.TASKORDER = #{order,javaType=int}
        
		  
            AND TWFTASKDEF.VIEWABLE = #{viewAble,javaType=boolean}
        
		  
            AND TWFTASKDEF.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.ALISE = #{alise,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFDID = #{wfdId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFTKDKEY = #{key,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM WF_TASK_DEF TWFTASKDEF
	
		  
            AND TWFTASKDEF.ID = #{id,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.TASKTYPE = #{taskType,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFTKDID = #{wftkdId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.TASKORDER = #{order,javaType=int}
        
		  
            AND TWFTASKDEF.VIEWABLE = #{viewAble,javaType=boolean}
        
		  
            AND TWFTASKDEF.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.ALISE = #{alise,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFDID = #{wfdId,javaType=java.lang.String}
        
		  
            AND TWFTASKDEF.WFTKDKEY = #{key,javaType=java.lang.String}
