mapper.select:
	SELECT 
			TWFPROCESSDEF.ID,
			TWFPROCESSDEF.CATEGORY,
			TWFPROCESSDEF.NAME,
			TWFPROCESSDEF.STATE,
			TWFPROCESSDEF.WFDID,
			TWFPROCESSDEF.SERVICETYPE,
			TWFPROCESSDEF.WFDKEY AS key,
			TWFPROCESSDEF.VERSION
	  FROM WF_PROCESS_DEF TWFPROCESSDEF
	
		  
            AND TWFPROCESSDEF.ID = #{id,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.CATEGORY = #{category,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.STATE = #{state,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.WFDID = #{wfdId,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.WFDKEY = #{key,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.VERSION = #{version,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM WF_PROCESS_DEF TWFPROCESSDEF
	
		  
            AND TWFPROCESSDEF.ID = #{id,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.CATEGORY = #{category,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.STATE = #{state,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.WFDID = #{wfdId,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.SERVICETYPE = #{serviceType,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.WFDKEY = #{key,javaType=java.lang.String}
        
		  
            AND TWFPROCESSDEF.VERSION = #{version,javaType=java.lang.String}
