SELECT TTBD.CODE, TTBD.VALID, TTBD.REMARK, TTBD.NAME, TTBD.TYPE_ AS type, TTBD.CREATEDATE, TTBD.LASTUPDATEDATE FROM TESTBASICDATA2 TTBD
AND TTBD.CODE = #{code,jdbcType=VARCHAR}
AND TTBD.NAME LIKE concat('%', #{name,jdbcType=VARCHAR}, '%')
AND TTBD.CODE <> #{excludeCode,javaType=java.lang.String}
ORDER BY #{orderSql}
ORDER BY CODE

